mapper.update:
UPDATE LAYER_GRID
GRIDS = #{grids}, FLOOR_AREA_ID = #{floor_area_id}
WHERE KID = #{kid} LIMIT 1

mapper.select:
select lg.kid,lg.floor_area_id,lg.grids,rt.areaName,rt.floorName,rt.fullName from LAYER_GRID lg inner join (select fa.kid,a.name areaName,fr.name floorName,CONCAT(fr.name,a.name) fullName from floor_area fa left join areas a on a.kid = fa.areaId left join floor fr on fr.kid = fa.floorId order by fr.sort,a.name) rt on rt.kid = lg.floor_area_id
rt.fullName LIKE CONCAT('%',#{name},'%')
ORDER BY ${column} ${order}
LIMIT #{section},#{pageSize}
